def run_server(model: str):
    if model == "sse":
        mcp.run(transform="sse", host="127.0.0.1", port=8080, path="/sse")
    elif model == "http":
        mcp.run(transport="streamable-http", host="127.0.0.1", port=8080, path="/mcp")
    else:
        mcp.run(port=8080)


if __name__ == "__main__":
    mcp.run('http')
